<?php//// Контроллер страницы чтение.//include ('m/M_User.php');class C_User extends C_Base{	//	// Конструктор.	//    public function action_info() {        $get_user = new M_User();        $user_info = $get_user->getUser($_SESSION['user_id']);        $this->title .= '::' . $user_info['user_name'];        $this->content = $this->Template('v/v_info.php', $user_info['user_name']);    }    public function action_reg() {        $this->title .= 'Регистрация';        $this->content = $this->Template('v/v_reg.php', array());        if($this->isPost()) {            $new_user = new M_User();            $result = $new_user->newUser($_POST['user_name'], $_POST['user_pswrd']);            if ($result) {                $this->content = $this->Template('v/v_reg.php', array('text' => $result));            } else {                $this->content = $this->Template('v/v_reg.php', array('text' => $result));            }        }    }    public function action_login() {        $this->title .= '::Вход';        $this->content = $this->Template('v/v.php', array());        if($this->isPost()) {            $login = new M_User();            $result = $login->login($_POST['user_name'], $_POST['user_pswrd']);            $this->content = $this->Template('v/v.php', array('text' => $result));        }    }    public function action_logout() {        $logout = new M_User();        $result = $logout->logout();    }}